import React, { useState, useEffect } from 'react';
import axios from 'axios';

const News = () => {
  const [newsType, setNewsType] = useState('financial');
  const [searchText, setSearchText] = useState('');
  const [newsData, setNewsData] = useState([]);
  const [selectedNews, setSelectedNews] = useState(null);
  const [likes, setLikes] = useState([]);
  const [collections, setCollections] = useState([]);
  const [noteInput, setNoteInput] = useState('');
  const apiKey = '8207fa107ccf4949a4ca9e07f5a3a039'; // Replace this with your News API key

  useEffect(() => {
    fetchNews();
  }, [newsType, searchText]);

  const fetchNews = async () => {
    try {
      const response = await axios.get(`https://newsapi.org/v2/everything?q=${newsType}&apiKey=${apiKey}`);
      setNewsData(response.data.articles);
    } catch (error) {
      console.error('Error fetching news:', error);
    }
  };

  const handleCategoryChange = (category) => {
    setNewsType(category);
  };

  const handleSearch = (event) => {
    setSearchText(event.target.value);
  };

  const handleNewsClick = (news) => {
    setSelectedNews(news);
  };

  const handleLike = (news) => {
    setLikes([...likes, news.title]);
  };

  const handleCollect = (news) => {
    setCollections([...collections, news.title]);
  };

  const handleAddNote = () => {
    // Add logic to save note for selected news
    console.log('Note added:', noteInput);
    setNoteInput('');
  };

  return (
    <div className="news-container">
      <div className="news-categories">
        <button onClick={() => handleCategoryChange('financial')}>Financial</button>
        <button onClick={() => handleCategoryChange('company')}>Company</button>
        <button onClick={() => handleCategoryChange('crypto')}>Crypto</button>
        <button onClick={() => handleCategoryChange('government')}>Government</button>
      </div>
      <div className="left-column">
        <h2>{newsType.toUpperCase()} News</h2>
        <p>{new Date().toLocaleDateString()}</p>
        <input type="text" placeholder="Search news..." value={searchText} onChange={handleSearch} />
        <ul>
          {newsData.map((news, index) => (
            <li key={index} onClick={() => handleNewsClick(news)}>
              {news.title}
            </li>
          ))}
        </ul>
      </div>
      <div className="right-column">
        {selectedNews && (
          <>
            <h2>{selectedNews.title}</h2>
            <p>{selectedNews.publishedAt}</p>
            <img src={selectedNews.urlToImage} alt="News Thumbnail" />
            <p>{selectedNews.content}</p>
            <div className="buttons">
              <button onClick={() => handleLike(selectedNews)}>Like</button>
              <button onClick={() => handleCollect(selectedNews)}>Collect</button>
              <button onClick={() => setNoteInput('')}>Add Note</button>
            </div>
            <textarea value={noteInput} onChange={(e) => setNoteInput(e.target.value)} placeholder="Add your note..." />
            <button onClick={handleAddNote}>Save Note</button>
          </>
        )}
      </div>
    </div>
  );
};

export default News;
